import React, { Component } from "react";
import { getMovies } from "../services/fakeMovieService";

class LikeBtn extends Component {
  state = {
    like: false,
  };

  handleLike() {
    const like = this.state.like;
    like = true;
    this.setState(like);
  }

  render() {
    return (
      <React.Fragment>
        <i className={this.toggleLike()} aria-hidden="true" />
      </React.Fragment>
    );
  }

  toggleLike() {
    let likeClass = "fa fa-heart";
    likeClass += like ? "fa fa-heart-o" : "fa-fa-heart";
    return likeClass;
  }
}

export default LikeBtn;
